package While;

public class For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(/*declaração da variavel*/int contador = 0; 
				/*expressao */ contador <= 20; 
				/*manipulação, neste caso incremento*/contador++) {
			System.out.printf("i = %d\n", contador);
		}

	}

}
