package fundamentos.lo;


public class TesteSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 0; i < 10; i++) {
			if(i % 2 == 1) {
			continue;
		
		}
		System.out.println(i);
	}
		
		for(int i = 0; i <= 10; i++) {
			if(i == 4) {
			continue;
		
		}
		System.out.println(i);
}
}
}
